t(f&quot;[*] &#x27;çº¦æŸ 2&#x27; å°†ä½¿ç”¨ä¸¤ä¸ªæ•°æ®èŒƒå›´:&quot;)    print(f&quot;    - æš‚æ€åŽ (slice1) èŒƒå›´: &#123;slice1_reliable_range&#125;&quot;)    print(f&quot;    - ç¨³å®š (slice2) èŒƒå›´: &#123;slice2_range&#125;&quot;)    # çº¦æŸ 3 (Printable flag) çš„èŒƒå›´    PRINTABLE_BYTES = set(string.printable.encode(&#x27;ascii&#x27;))    # éšæœºæ•°æ®éƒ¨åˆ†ä¸åŒ…å« 0x00    PRINTABLE_ONLY = PRINTABLE_BYTES - &#123;0&#125; if 0 in PRINTABLE_BYTES else PRINTABLE_BYTES    # --- 2. å»ºç«‹æ‰€éœ€å¯†é’¥çš„å€™é€‰åˆ—è¡¨ ---    # èŽ·å– flag_body (flag&#123;...&#125; ä¸­ ... çš„éƒ¨åˆ†) æ‰€éœ€çš„å¯†é’¥ä½ç½®    flag_body_key_positions = []    for i in range(len(flag_body_ct)):        flag_body_key_positions.append((flag_body_start_idx + i) % PERIOD)    unique_key_positions = set(flag_body_key_positions)    # key_candidates: å­˜å‚¨æ¯ä¸ª *å¿…éœ€* å¯†é’¥ä½ç½®çš„å¯èƒ½å€¼    key_candidates = &#123;pos: set(range(256)) for pos in unique_key_positions&#125;    print(f&quot;[*] Flag ä¸»ä½“ (&#123;len(flag_body_ct)&#125; å­—èŠ‚) ä¾èµ–äºŽ &#123;len(unique_key_positions)&#125; ä¸ªå”¯ä¸€çš„å¯†é’¥å­—èŠ‚ã€‚&quot;)    # --- 3. åº”ç”¨çº¦æŸ 1 (å·²çŸ¥æ˜Žæ–‡) ---    print(f&quot;[*] æ­£åœ¨åº”ç”¨â€œçº¦æŸ 1â€(å·²çŸ¥æ˜Žæ–‡)...&quot;)    known_pt = &#123;&#125;    # b&#x27;\x00&#x27; at start of flag block    known_pt[L_RANDOM_PART1] = 0x00    # b&#x27;flag&#123;&#x27;    prefix = b&#x27;flag&#123;&#x27;    for i in range(len(prefix)):        known_pt[L_RANDOM_PART1 + 1 + i] = prefix[i]    # b&#x27;\x00&#x27; at end of flag block    known_pt[L_RANDOM_PART1 + 1 + L_flag] = 0x00    # b&#x27;\x00\x00\x00\x00&#x27; at end of file    for i in range(4):        known_pt[L_ct - 4 + i] = 0x00    # ç”¨å·²çŸ¥æ˜Žæ–‡è¿‡æ»¤ key_candidates    for idx, pt_byte in known_pt.items():        pos = idx % PERIOD        if pos in key_candidates:            key_byte = ct[idx] ^ pt_byte            # è¿™æ˜¯ä¸€ä¸ªå¼ºçº¦æŸï¼Œæˆ‘ä»¬åªä¿ç•™è¿™ä¸€ä¸ªå€¼            key_candidates[pos].intersection_update(&#123;key_byte&#125;)    