# --- 4. åº”ç”¨çº¦æŸ 2 (Printable msg) ---    print(f&quot;[*] æ­£åœ¨åº”ç”¨â€œçº¦æŸ 2â€(Printable msg parts)...&quot;)    def filter_set_with_byte(guesses_set, ct_byte, constraints_set):        &quot;&quot;&quot;è¾…åŠ©å‡½æ•°ï¼šç”¨ä¸€ä¸ªå¯†æ–‡å­—èŠ‚è¿‡æ»¤ä¸€ä¸ªçŒœæµ‹é›†&quot;&quot;&quot;        # å¦‚æžœçŒœæµ‹é›†å·²ç»åªæœ‰ä¸€ä¸ªå€¼ï¼Œå°±è·³è¿‡        if len(guesses_set) &lt;= 1:            return        current_guesses = guesses_set.copy()        for k_guess in current_guesses:            # éšæœºæ•°æ®ä¸åŒ…å« 0x00ï¼Œæ‰€ä»¥ä½¿ç”¨ PRINTABLE_ONLY            if (ct_byte ^ k_guess) not in PRINTABLE_ONLY:                guesses_set.remove(k_guess)    # éåŽ†æ‰€æœ‰éœ€è¦çš„å¯†é’¥ä½ç½®    for pos in tqdm(key_candidates.keys(), desc=&quot;Filtering keys&quot;):        if len(key_candidates[pos]) == 1:            continue        # éåŽ† msg_part1 (åŽ 60%)        start_idx = slice1_reliable_range[0]        end_idx = slice1_reliable_range[1]        # (é«˜æ•ˆéåŽ†, ä»Ž &#x27;pos&#x27; åœ¨èŒƒå›´å†…çš„ç¬¬ä¸€ä¸ªç´¢å¼•å¼€å§‹)        first_i = start_idx + (pos - start_idx) % PERIOD        for i in range(first_i, end_idx, PERIOD):            filter_set_with_byte(key_candidates[pos], ct[i], PRINTABLE_ONLY)        # éåŽ† msg_part2        start_idx = slice2_range[0]        end_idx = slice2_range[1]        first_i = start_idx + (pos - start_idx) % PERIOD        for i in range(first_i, end_idx, PERIOD):            filter_set_with_byte(key_candidates[pos], ct[i], PRINTABLE_ONLY)    # --- 5. æ£€æŸ¥ç»“æžœå¹¶è¿›è¡Œæš´åŠ›ç ´è§£ ---    print(&quot;\n[*] çº¦æŸåº”ç”¨å®Œæˆã€‚æ­£åœ¨æ£€æŸ¥å‰©ä½™å¯èƒ½æ€§...&quot;)    total_solutions = 1    has_contradiction = False    unresolved_keys = &#123;&#125;  # å­˜å‚¨å¤šè§£çš„å¯†é’¥    for pos in unique_key_positions:        count = len(key_candidates[pos])        if count == 0:            print(f&quot;[!] è‡´å‘½é”™è¯¯: å¯†é’¥ä½ç½® &#123;pos&#125; æ²¡æœ‰å¯èƒ½çš„è§£ï¼(å‡ºçŽ°çŸ›ç›¾)&quot;)            has_contradiction = True        elif count &gt; 1:            print(f&