import osimport hashlibimport randomfrom Crypto.Cipher import AESfrom Crypto.Util.Padding import padfrom ecdsa import SECP256k1from ecdsa.ecdsa import Public_key, Private_key, Signaturecurve = SECP256k1G = curve.generatorn = curve.orderd = random.randint(1, n-1)pub_point = d * Gaes_key = hashlib.sha256(str(d).encode()).digest()flag_str = os.getenv(&quot;GZCTF_FLAG&quot;, &quot;flag&#123;test_flag&#125;&quot;)FLAG = flag_str.encode()def get_signature(msg_bytes, k_nonce):    h = hashlib.sha256(msg_bytes).digest()    z = int.from_bytes(h, &#x27;big&#x27;)    k_point = k_nonce * G    r = k_point.x() % n    k_inv = pow(k_nonce, -1, n)    s = (k_inv * (z + r * d)) % n    return (r, s)def main():    print(&quot;Welcome to the Lazy ECDSA Signer!&quot;)    print(&quot;I can sign any message for you, but I won&#x27;t give you the flag directly.&quot;)    cipher = AES.new(aes_key, AES.MODE_ECB)    encrypted_flag = cipher.encrypt(pad(FLAG, 16))    print(f&quot;Encrypted Flag (hex): &#123;encrypted_flag.hex()&#125;&quot;)    k_nonce = random.randint(1, n-1)    while True:        try:            print(&quot;\n[1] Sign a message&quot;)            print(&quot;[2] Exit&quot;)            choice = input(&quot;Option: &quot;).strip()            if choice == &#x27;1&#x27;:                msg = input(&quot;Enter message to sign: &quot;).strip()                if not msg: continue                r, s = get_signature(msg.encode(), k_nonce)                print(f&quot;Signature (r, s): (&#123;r&#125;, &#123;s&#125;)&quot;)            else:                break        except Exception as e:            print(&quot;Error.&quot;)            breakif __name__ == &quot;__main__&quot;:    main()
